@use "colors";
@import url("https://css.gg/chevron-double-down.css");
@import url("https://css.gg/chevron-left.css");
@import url("https://css.gg/chevron-right.css");
@import url("https://css.gg/arrow-right-o.css");
@import url("https://css.gg/play-button-o.css");
@import url("https://css.gg/play-pause-o.css");
@import url("https://css.gg/play-pause.css");

$concertRed: url("../images/concert.jpg");
$concert: url("../images/concert2.jpg");
$hero: url("../images/inspire.jpg");
$pandaPlaying: url("../images/panda.jpg");
$solo: url("../images/solo.jpg");

@mixin setImage($image, $color) {
  height: 100vh;
  background-repeat: no-repeat;
  background-image: $image;
  background-position: center;
  background-size: cover;
  background-color: $color;
  background-blend-mode: overlay;
  background-attachment: fixed;

  @if (lightness($color) <90) {
    color: colors.$white;
  } @else {
    color: colors.$black-dark;
  }
}
$concert1: url("../images/concerts/sum41.jpg");
$concert2: url("../images/concerts/greenday.jpg");
$concert3: url("../images/concerts/theofspring.jpg");
$concert4: url("../images/concerts/riseagainst.jpg");
$concert5: url("../images/concerts/skillet.jpg");
$concert6: url("../images/concerts/slipknot.jpg");

@mixin setConcert($image, $color) {
  background-repeat: no-repeat;
  background-image: $image;
  background-position: center;
  background-size: cover;
  background-color: $color;
  background-blend-mode: overlay;
  background-attachment: fixed;
  width: 100%;
  height: 400px;

  filter: grayscale(1);
}

.gg-play-button-o,
.gg-play-pause-o {
  width: 50px;
}

